    page4Content1 = `{"id":"104339337","type":"page","status":"current","title":"Dialogs","body":{"view":{"value":"    \n \n<a name=\"composition-deck-switcher\"></a>\n<div id=\"switcher\"\n     class=\"aui-tabs horizontal-tabs deck \"\n     history=\"true\" \n     loopcards=\"false\" \n     effecttype=\"\" \n     effectduration=\"1.0\"\n     nextafter=\"0.0\">\n\n    \t\t<ul class=\"tabs-menu \"></ul>\n        <div class=\"deck-cards\" style=\"\">\t<div id=\"86836\" \n     class=\"tabs-pane active-pane\"\n     style=\"\" \n     cssclass=\"\"\n\t accesskey=\"\"\n\t label=\"Web\"\n\t title=\"\"\n\t nextafter=\"0\"\n\t effecttype=\"\"\n\t effectduration=\"-1.0\"><p>&nbsp;</p>\n<div class=\"contentLayout2\"> \n <div class=\"columnLayout single\" data-layout=\"single\"> \n  <div class=\"cell normal\" data-type=\"normal\"> \n   <div class=\"innerCell\"> \n    <div class=\"navmenu\" style=\"float:right; width:150px; background:#efefef; margin:0px 0px 0px 10px; padding:15px 10px 50px 15px\">\n     <p><style type=\"text/css\">/*<![CDATA[*/\ndiv.rbtoc1519912709241 {padding: 0px;}\ndiv.rbtoc1519912709241 ul {list-style: none;margin-left: 0px;padding-left: 15px;}\ndiv.rbtoc1519912709241 li {margin-left: 0px;padding-left: 0px;}\n\n/*]]>*/</style></p>\n     <div class=\"toc-macro rbtoc1519912709241\"> \n      <ul class=\"toc-indentation\"> \n       <li><a href=\"https://confluence.ultimatesoftware.com/pages/viewpage.action?pageId=108273643#Dialogs(web)-Use\">Use</a></li> \n       <li><a href=\"https://confluence.ultimatesoftware.com/pages/viewpage.action?pageId=108273643#Dialogs(web)-Code\">Code</a></li> \n       <li><a href=\"https://confluence.ultimatesoftware.com/pages/viewpage.action?pageId=108273643#Dialogs(web)-Types\">Types</a> \n        <ul class=\"toc-indentation\"> \n         <li><a href=\"https://confluence.ultimatesoftware.com/pages/viewpage.action?pageId=108273643#Dialogs(web)-ConfirmationDialogConfirmationdialogs\">Confirmation dialogs</a></li> \n         <li><a href=\"https://confluence.ultimatesoftware.com/pages/viewpage.action?pageId=108273643#Dialogs(web)-SimpleDialogSimpledialog\">Simple dialog</a></li> \n         <li><a href=\"https://confluence.ultimatesoftware.com/pages/viewpage.action?pageId=108273643#Dialogs(web)-Formsindialog\">Forms in dialog</a></li> \n        </ul> </li> \n       <li><a href=\"https://confluence.ultimatesoftware.com/pages/viewpage.action?pageId=108273643#Dialogs(web)-Contentandbehavior\">Content and behavior</a></li> \n       <li><a href=\"https://confluence.ultimatesoftware.com/pages/viewpage.action?pageId=108273643#Dialogs(web)-Style\">Style</a> \n        <ul class=\"toc-indentation\"> \n         <li><a href=\"https://confluence.ultimatesoftware.com/pages/viewpage.action?pageId=108273643#Dialogs(web)-Size\">Size</a></li> \n        </ul> </li> \n      </ul> \n     </div>\n     <p></p>\n    </div>\n    <div class=\"navmenu\" style=\"float:left; width:150px; background:white; margin:0px 10px 0px 0px; padding:3px\">\n     <p></p>\n     <p><span class=\"confluence-embedded-file-wrapper confluence-embedded-manual-size\"><img class=\"confluence-embedded-image confluence-external-resource confluence-content-image-border\" width=\"150\" src=\"https://ultimatesoftware.invisionapp.com/static-signed/live-embed/8771451/259379647/2/latest/ZSast8lEnFyRnnli8olEM3qXfkLlE74BrJRnwmwxLwuWCpzrmQrB8WifJsWnWVckBylLWhz14tRlEv10KWUv2fkJ0AlE/Dialog-thumb.png\" data-image-src=\"https://ultimatesoftware.invisionapp.com/static-signed/live-embed/8771451/259379647/2/latest/ZSast8lEnFyRnnli8olEM3qXfkLlE74BrJRnwmwxLwuWCpzrmQrB8WifJsWnWVckBylLWhz14tRlEv10KWUv2fkJ0AlE/Dialog-thumb.png\" /></span></p>\n     <div class=\"navmenu\" style=\"float:right; background:white; margin:3px; padding:3px\">\n      <p> <span class=\"jira-issue resolved\"> <a href=\"https://ultidev/browse/NU-5074?src=confmacro\" class=\"jira-issue-key\"><img class=\"icon\" src=\"https://ultidev/images/icons/issuetypes/subtask.png\" />NU-5074</a> - <span class=\"summary\">Dialog</span> \n        <!-- The new status lozengens support for JIRA 6.2 or later. Otherwise we use the icon.--> <span class=\"aui-lozenge aui-lozenge-subtle aui-lozenge-success jira-macro-single-issue-export-pdf\">Closed</span> </span> </p>\n     </div>\n     <div class=\"navmenu\" style=\"float:right; background:white; margin:3px; padding:3px\">\n      <p><a href=\"https://ultimatesoftware.invisionapp.com/share/CNDZ7ZX27#/255168759_Cards_-_Dialogs\" class=\"external-link\" rel=\"nofollow\"><span class=\"status-macro aui-lozenge aui-lozenge-success aui-lozenge-subtle\">SKETCH...</span></a><a href=\"https://ultimatesoftware.invisionapp.com/share/CXDU798NA\" class=\"external-link\" rel=\"nofollow\"></a></p>\n     </div>\n     <div class=\"navmenu\" style=\"float:right; background:white; margin:3px; padding:3px\">\n      <p><a href=\"https://ultimatesoftware.invisionapp.com/share/CNDZ7ZX27#/255168759_Cards_-_Dialogs\" class=\"external-link\" rel=\"nofollow\"><span class=\"status-macro aui-lozenge aui-lozenge-success aui-lozenge-subtle\">INVISION...</span></a><a href=\"https://ultimatesoftware.invisionapp.com/share/CXDU798NA\" class=\"external-link\" rel=\"nofollow\"></a></p>\n     </div>\n     <p></p>\n    </div>\n    <p><span style=\"color: rgb(33,33,33);\">Dialogs inform users about a specific task and may contain critical information, require decisions, or involve multiple tasks.</span>&nbsp;<em>see&nbsp;<a href=\"https://material.io/guidelines/components/dialogs.html\" class=\"external-link\" rel=\"nofollow\">Material Design Dialogs</a>&nbsp;and&nbsp;<a href=\"https://material.angular.io/components/dialog/overview\" class=\"external-link\" rel=\"nofollow\">Angular Material Dialogs</a></em></p>\n   </div> \n  </div> \n </div> \n <div class=\"columnLayout single\" data-layout=\"single\"> \n  <div class=\"cell normal\" data-type=\"normal\"> \n   <div class=\"innerCell\"> \n    <hr />\n    <h2 id=\"Dialogs-Use\">Use</h2>\n    <div class=\"navmenu\" style=\"float:right; width:350px; background:white; margin:0 0 0 16px; padding:3px\">\n     <p><span class=\"confluence-embedded-file-wrapper confluence-embedded-manual-size\"><img class=\"confluence-embedded-image confluence-external-resource\" alt=\"Simple dialog\" height=\"150\" src=\"https://ultimatesoftware.invisionapp.com/static-signed/live-embed/8771451/259379645/2/latest/171u1AqiQ0oKaskoclEHolsluw3kL5j3wKRGa1LnHfrjjZGOmGUUbXOutU9igVt4ZlptmJPtBKGkXnwve7QM1QlE/Simple-dialog.png\" data-image-src=\"https://ultimatesoftware.invisionapp.com/static-signed/live-embed/8771451/259379645/2/latest/171u1AqiQ0oKaskoclEHolsluw3kL5j3wKRGa1LnHfrjjZGOmGUUbXOutU9igVt4ZlptmJPtBKGkXnwve7QM1QlE/Simple-dialog.png\" /></span><span class=\"confluence-embedded-file-wrapper confluence-embedded-manual-size\"><img class=\"confluence-embedded-image confluence-thumbnail\" width=\"200\" src=\"/download/thumbnails/108273643/Screen%20Shot%202018-01-10%20at%201.20.11%20PM.png?version=2&amp;modificationDate=1515608809750&amp;api=v2\" data-image-src=\"/download/attachments/108273643/Screen%20Shot%202018-01-10%20at%201.20.11%20PM.png?version=2&amp;modificationDate=1515608809750&amp;api=v2\" data-unresolved-comment-count=\"0\" data-linked-resource-id=\"109154829\" data-linked-resource-version=\"2\" data-linked-resource-type=\"attachment\" data-linked-resource-default-alias=\"Screen Shot 2018-01-10 at 1.20.11 PM.png\" data-base-url=\"https://confluence.ultimatesoftware.com\" data-linked-resource-content-type=\"image/png\" data-linked-resource-container-id=\"108273643\" data-linked-resource-container-version=\"38\" /></span><span class=\"confluence-embedded-file-wrapper confluence-embedded-manual-size\"><img class=\"confluence-embedded-image confluence-external-resource\" alt=\"Confirm dialog with title\" height=\"150\" src=\"https://ultimatesoftware.invisionapp.com/static-signed/live-embed/8771451/259379647/2/latest/ZSast8lEnFyRnnli8olEM3qXfkLlE74BrJRnwmwxLwuWCpzrmQrB8WifJsWnWVckBylLWhz14tRlEv10KWUv2fkJ0AlE/Dialog-thumb.png\" data-image-src=\"https://ultimatesoftware.invisionapp.com/static-signed/live-embed/8771451/259379647/2/latest/ZSast8lEnFyRnnli8olEM3qXfkLlE74BrJRnwmwxLwuWCpzrmQrB8WifJsWnWVckBylLWhz14tRlEv10KWUv2fkJ0AlE/Dialog-thumb.png\" /></span></p>\n     <p>&nbsp;</p>\n    </div>\n    <p>Use dialogs to progressively disclose additional information, prompt users to take action or confirm choices that may be destructive or have far reaching consequences. Dialogs can also be used to present modal forms when it is important user stays in context.</p>\n    <p><strong><span style=\"color: rgb(0,128,0);\">Do</span></strong></p>\n    <ol>\n     <li>Do use flat buttons for dialog actions</li>\n     <li>Do use short, succinct labels that are verbs</li>\n     <li>Do use a titles on the dialog when it helps organize the description</li>\n     <li>Do use only a description when a title isn't necessary</li>\n     <li>For Simple dialogs, use only description text without a title</li>\n     <li>Do use confirmation dialogs to inform the user of consequences or clarify desired action(s).</li>\n    </ol>\n    <p><strong><span style=\"color: rgb(255,0,0);\">Don't</span></strong></p>\n    <ol>\n     <li>Don't use raised buttons (except for forms in dialogs)</li>\n     <li>Don't use a close button or a dark background on the simple dialog</li>\n     <li>Don't use &quot;Yes&quot; or &quot;No&quot; for button actions, instead use verbs.</li>\n     <li>Don't use a close icon</li>\n    </ol>\n   </div> \n  </div> \n </div> \n <div class=\"columnLayout single\" data-layout=\"single\"> \n  <div class=\"cell normal\" data-type=\"normal\"> \n   <div class=\"innerCell\"> \n    <hr />\n    <h2 id=\"Dialogs-Code\">Code</h2>\n    <p><a href=\"https://material.angular.io/components/dialog/overview\" class=\"external-link\" rel=\"nofollow\">Angular Material Dialogs &gt;</a></p> \n    <a name=\"composition-deck-CheckboxTypes\"></a> \n    <div id=\"CheckboxTypes\" class=\"aui-tabs horizontal-tabs deck \" history=\"true\" loopcards=\"false\" effecttype=\"\" effectduration=\"1.0\" nextafter=\"0.0\"> \n     <ul class=\"tabs-menu \"></ul> \n     <div class=\"deck-cards\" style=\"\"> \n      <div id=\"1346468776\" class=\"tabs-pane\" style=\"\" cssclass=\"\" accesskey=\"\" label=\"Preview\" title=\"\" nextafter=\"0\" effecttype=\"\" effectduration=\"-1.0\">\n       <iframe src=\"https://ignite-sandbox-mb.apps.mia.ulti.io/#/dialogs\" style=\"width: 100%; height: 250px;border: 0px solid #ddd;\" scrolling=\"no\"></iframe>\n      </div> \n      <p>&nbsp;</p>\n      <p>&nbsp;</p> \n      <div id=\"-1645677095\" class=\"tabs-pane\" style=\"\" cssclass=\"\" accesskey=\"\" label=\"Angular docs\" title=\"\" nextafter=\"0\" effecttype=\"\" effectduration=\"-1.0\">\n       <iframe src=\"https://material.angular.io/components/select/overview\" style=\"width: 100%; min-height: 3500px;border: 1px solid #ddd; margin: 0 -16px;\"></iframe>\n      </div> \n     </div> \n    </div> \n    <!-- // .deck --> \n    <p>&nbsp;</p>\n    <p>&nbsp;</p>\n   </div> \n  </div> \n </div> \n <div class=\"columnLayout single\" data-layout=\"single\"> \n  <div class=\"cell normal\" data-type=\"normal\"> \n   <div class=\"innerCell\"> \n    <hr />\n    <h2 id=\"Dialogs-Types\">Types</h2>\n    <p>There are two types of dialogs with a few options:&nbsp;<a href=\"#Dialogs-ConfirmationDialog\">confirmation dialogs</a>, and&nbsp;<a href=\"#Dialogs-SimpleDialog\">simple dialogs</a></p>\n    <div class=\"navmenu\" style=\"float:right; background:white; margin:0 0 0 16px; padding:3px\">\n     <p><span class=\"confluence-embedded-file-wrapper confluence-embedded-manual-size\"><img class=\"confluence-embedded-image confluence-thumbnail\" height=\"150\" src=\"/download/thumbnails/108273643/Screen%20Shot%202018-01-10%20at%201.20.11%20PM.png?`;
    page4Content2 = `version=2&amp;modificationDate=1515608809750&amp;api=v2\" data-image-src=\"/download/attachments/108273643/Screen%20Shot%202018-01-10%20at%201.20.11%20PM.png?version=2&amp;modificationDate=1515608809750&amp;api=v2\" data-unresolved-comment-count=\"0\" data-linked-resource-id=\"109154829\" data-linked-resource-version=\"2\" data-linked-resource-type=\"attachment\" data-linked-resource-default-alias=\"Screen Shot 2018-01-10 at 1.20.11 PM.png\" data-base-url=\"https://confluence.ultimatesoftware.com\" data-linked-resource-content-type=\"image/png\" data-linked-resource-container-id=\"108273643\" data-linked-resource-container-version=\"38\" /></span></p>\n    </div>\n    <h3 id=\"Dialogs-ConfirmationDialogConfirmationdialogs\"><span class=\"confluence-anchor-link\" id=\"Dialogs-ConfirmationDialog\"></span>Confirmation dialogs</h3>\n    <p>Confirmation dialogs&nbsp;require users to explicitly confirm a choice. It&nbsp;is a modal that requires action before the system can move on.&nbsp;Confirmations appear modally (on a darkened background (called a scrim)) to indicate the content below is no longer interactive.&nbsp;</p>\n    <p>Options: with or without titles</p>\n    <div class=\"sectionMacro\">\n     <div class=\"sectionMacroRow\">\n      <p>See below for content</p>\n     </div>\n    </div>\n    <div class=\"navmenu\" style=\"float:right; background:white; margin:0 0 0 16px; padding:3px\">\n     <p><span class=\"confluence-embedded-file-wrapper confluence-embedded-manual-size\"><img class=\"confluence-embedded-image confluence-external-resource\" height=\"250\" width=\"250\" src=\"https://ultimatesoftware.invisionapp.com/static-signed/live-embed/8771451/259379647/2/latest/ZSast8lEnFyRnnli8olEM3qXfkLlE74BrJRnwmwxLwuWCpzrmQrB8WifJsWnWVckBylLWhz14tRlEv10KWUv2fkJ0AlE/Dialog-thumb.png\" data-image-src=\"https://ultimatesoftware.invisionapp.com/static-signed/live-embed/8771451/259379647/2/latest/ZSast8lEnFyRnnli8olEM3qXfkLlE74BrJRnwmwxLwuWCpzrmQrB8WifJsWnWVckBylLWhz14tRlEv10KWUv2fkJ0AlE/Dialog-thumb.png\" /></span></p>\n     <p>&nbsp;</p>\n    </div>\n    <div class=\"sectionMacro\">\n     <div class=\"sectionMacroRow\">\n      <p>&nbsp;</p>\n     </div>\n    </div>\n    <div class=\"navmenu\" style=\"float:right; background:white; margin:0 0 0 16px; padding:3px\">\n     <p><span class=\"confluence-embedded-file-wrapper confluence-embedded-manual-size\"><img class=\"confluence-embedded-image confluence-external-resource\" height=\"250\" src=\"https://ultimatesoftware.invisionapp.com/static-signed/live-embed/8771451/259379645/2/latest/171u1AqiQ0oKaskoclEHolsluw3kL5j3wKRGa1LnHfrjjZGOmGUUbXOutU9igVt4ZlptmJPtBKGkXnwve7QM1QlE/Simple-dialog.png\" data-image-src=\"https://ultimatesoftware.invisionapp.com/static-signed/live-embed/8771451/259379645/2/latest/171u1AqiQ0oKaskoclEHolsluw3kL5j3wKRGa1LnHfrjjZGOmGUUbXOutU9igVt4ZlptmJPtBKGkXnwve7QM1QlE/Simple-dialog.png\" /></span></p>\n     <p>&nbsp;</p>\n    </div>\n    <h3 id=\"Dialogs-SimpleDialogSimpledialog\"><span class=\"confluence-anchor-link\" id=\"Dialogs-SimpleDialog\"></span>Simple dialog</h3>\n    <p>Simple dialogs provide in-context assistance such as a definition of a complex term, details, or actions about a list item. Their intent is to help the user understand something that may not be clear without interrupting the users flow.</p>\n    <h4 id=\"Dialogs-Implementation\">Implementation</h4>\n    <p>Simple dialogs appears below and to the right of an icon on mouseover and selection. Unlike the confirmation, the background is not usually darkened (however it will be for short term) . It remains open as long as the user is hovering over the icon or the content inside. It no longer appears when the user mouses away from either the icon or content. It does not require dismissal for the user to interact with the page (e.g. if keyboard focus is on a field, the field remains active).</p>\n    <h3 id=\"Dialogs-Formsindialog\">Forms in dialog</h3>\n    <ul>\n     <li>Forms can be presented in a dialog (aka modal screen) when it's important to keep context and the form is relatively small. For large or multi-step forms, navigate user to a new view page.&nbsp;</li>\n     <li>Forms in dialog should follow the same layout as forms not in dialogs</li>\n     <li>when cancel and delete are actions for the form, present a confirmation dialog when users attempt to execute a destructive action, cancel (discard information) or delete. This dialog will come over top of the form within the dialog:</li>\n     <li><span class=\"confluence-embedded-file-wrapper confluence-embedded-manual-size\"><img class=\"confluence-embedded-image\" height=\"250\" src=\"/download/attachments/108273643/Screen%20Shot%202018-01-04%20at%2011.22.36%20AM.png?version=1&amp;modificationDate=1515082971313&amp;api=v2\" data-image-src=\"/download/attachments/108273643/Screen%20Shot%202018-01-04%20at%2011.22.36%20AM.png?version=1&amp;modificationDate=1515082971313&amp;api=v2\" data-unresolved-comment-count=\"0\" data-linked-resource-id=\"109152085\" data-linked-resource-version=\"1\" data-linked-resource-type=\"attachment\" data-linked-resource-default-alias=\"Screen Shot 2018-01-04 at 11.22.36 AM.png\" data-base-url=\"https://confluence.ultimatesoftware.com\" data-linked-resource-content-type=\"image/png\" data-linked-resource-container-id=\"108273643\" data-linked-resource-container-version=\"38\" /></span>&nbsp;</li>\n    </ul>\n    <p>&nbsp;</p>\n   </div> \n  </div> \n </div> \n <div class=\"columnLayout single\" data-layout=\"single\"> \n  <div class=\"cell normal\" data-type=\"normal\"> \n   <div class=\"innerCell\"> \n    <hr />\n    <h2 id=\"Dialogs-Contentandbehavior\">Content and behavior</h2>\n    <ul>\n     <li>CANCEL Confirmation</li>\n     <ul>\n      <li>If no changes to form (e.g. not dirty) simply discard changes, do not give user a confirmation and navigate back to parent page (if applicable)</li>\n      <li><em>Confirmation dialog</em>: If changes (e.g., form dirty), show a confirmation dialog:</li>\n      <ul>\n       <li>Message</li>\n       <ul>\n        <li>You have unsaved changes. Are you sure you want to cancel?</li>\n       </ul>\n       <li>Actions &amp; behavior</li>\n       <ul>\n        <li>DISCARD</li>\n        <ul>\n         <li>throws away all changes and navigates back to parent page (if applicable)</li>\n         <li>(no color treatment e.g., black)\\</li>\n        </ul>\n        <li>SAVE (or submit)</li>\n        <ul>\n         <li>functions identically to form action; e.g.,</li>\n         <ul>\n          <li>saves changes and navigates back to parent or</li>\n          <li>shows page and field level errors (if errors)</li>\n         </ul>\n        </ul>\n       </ul>\n      </ul>\n     </ul>\n     <li>DELETE Confirmation</li>\n     <ul>\n      <li>Only shows on edit (not add (create new))</li>\n      <li>Only shows if user can delete object</li>\n      <li>Confirmation dialog</li>\n      <ul>\n       <li>Title: Delete [object name]?</li>\n       <li>Message: This will permanently delete [object name] and remove any associations.</li>\n       <li>Actions &amp; behavior</li>\n       <ul>\n        <li>CANCEL</li>\n        <ul>\n         <li>closes dialog, user remains on screen</li>\n        </ul>\n        <li>DELETE</li>\n        <ul>\n         <li>deletes object and navigates user to parent page</li>\n        </ul>\n       </ul>\n      </ul>\n     </ul>\n    </ul>\n   </div> \n  </div> \n </div> \n <div class=\"columnLayout single\" data-layout=\"single\"> \n  <div class=\"cell normal\" data-type=\"normal\"> \n   <div class=\"innerCell\"> \n    <hr />\n    <h2 id=\"Dialogs-Style\">Style</h2>\n    <h3 id=\"Dialogs-Size\">Size</h3>\n    <ul>\n     <li>Confirmation\n      <ul>\n       <li>maxWidth: 360px;</li>\n       <li>height: (auto)</li>\n      </ul></li>\n     <li>Simple: (TBD based on context)</li>\n     <li>Forms\n      <ul>\n       <li>maxWidth: 672px;</li>\n       <li>height: <em>(in research</em>)</li>\n       <li>dialogs should not take 100% of the screen width or height (except in modal)</li>\n       <li>for implementing size:&nbsp;<em><em>see&nbsp;</em></em><a href=\"https://material.angular.io/components/form-field/overview\" class=\"external-link\" rel=\"nofollow\">MatDialogConfig </a>in Angular Material docs for dialogs.</li>\n      </ul></li>\n    </ul>\n    <p>&nbsp;</p>\n   </div> \n  </div> \n </div> \n</div>\n<p></p></div>\n\t<div id=\"-1984987966\" \n     class=\"tabs-pane\"\n     style=\"\" \n     cssclass=\"\"\n\t accesskey=\"\"\n\t label=\"Mobile\"\n\t title=\"\"\n\t nextafter=\"0\"\n\t effecttype=\"\"\n\t effectduration=\"-1.0\"><p><em>in progress</em></p></div>\n<p> </p><p> </p></div>\n    </div> <!-- // .deck -->","representation":"view","_expandable":{"content":"/rest/api/content/104339337"}},"_expandable":{"editor":"","export_view":"","storage":"","anonymous_export_view":""}},"extensions":{"position":"none"},"_links":{"webui":"/display/IDS/Dialogs","tinyui":"/x/iRc4Bg","collection":"/rest/api/content","base":"https://confluence.ultimatesoftware.com","context":"","self":"https://confluence.ultimatesoftware.com/rest/api/content/104339337"},"_expandable":{"container":"","metadata":"","operations":"","children":"/rest/api/content/104339337/child","history":"/rest/api/content/104339337/history","ancestors":"","version":"","descendants":"/rest/api/content/104339337/descendant","space":"/rest/api/space/IDS"}}`;
